import {useState} from 'react'
import Layout from '../components/layout'
import fetch from 'isomorphic-unfetch'

const Detailed = ()=>{
    const [currency, setCurrency]= useState("USD")
    const [value, setValue] = useState("100")
    const fetchData = async ()=>{
        console.log(currency)
        console.log(value)
        const res = await fetch(`https://blockchain.info/tobtc?currency=${currency}&value=${value}`)
        console.log(res)
    }

    return(
        <Layout>
            <h1> Check out price</h1>
            <form>
                <select name="currency" id="currency" onChange={e=> setCurrency(e.target.value)}>
                    <option value="USD"> USD </option>
                    <option value="GBP"> GBP </option>
                    <option value="EUR"> EUR </option>
                </select>
                {/* <input type="number" onChange = {e=> setValue(e.target.value)}>{value}</input>
                <input type="submit" onClick={()=>fetchData()}> Submit </input> */}
            </form>

        </Layout>
    )
}

