import {useState} from 'react'
import Layout from '../components/layout'
import { fetchData } from '../components/data'

const Detailed = ()=>{
    const [currency, setCurrency]= useState("USD")
    const [value, setValue] = useState("100")
    const [data, setData] = useState(null)

    const onClickHandler = async ()=>{
        const data = await fetchData(currency, value)
        console.log(data)

    }


    return(
        <Layout>
            <h1> Check out price</h1>

                <select name="currency" id="currency" onChange={e=> setCurrency(e.target.value)}>
                    <option value="USD"> USD </option>
                    <option value="GBP"> GBP </option>
                    <option value="EUR"> EUR </option>
                </select>
                <button onClick={()=>onClickHandler()}> Confirm </button>

                <p><span> {currency} </span> <span> 100 </span></p>

        </Layout>
    )
}



export default Detailed
